    <Layout>
      <div>
        <button><a href="/create">New Post</a></button>
      </div>
      <div className="page">
        <h1>Public Feed</h1>
        <main>
          {props.feed.map((post) => (
            <div key={post.id} className="post">
              <Post post={post} />
